const JWT = "store_token_id";

const setToken = (token) => {
  localStorage.setItem(JWT, token);
};

const getToken = (token) => {
  localStorage.getItem(JWT, token);
};

const getUser = () => {
  const jwToken = getToken();
};

global.auth = {
  setToken,
};
