import React, { Component } from "react";
import axios from "commons/axios";
import Layout from "Layout";
import CartItem from "component/CartItem";

export default class cart extends Component {
  state = {
    carts: [],
  };
  componentDidMount() {
    axios.get("/carts").then((res) =>
      this.setState({
        carts: res.data,
      })
    );
  }

  totalPrice = () => {
    const totalPrice = this.state.carts
      .map((cart) => cart.mount * parseInt(cart.price))
      .reduce((a, value) => a + value, 0);
    console.log(totalPrice);
    return totalPrice;
  };

  updateCart = (cart) => {
    const newCarts = [...this.state.carts]; //複製新的購物車
    const _index = newCarts.findIndex((c) => c.id === cart.id);
    newCarts.splice(_index, 1, cart);
    this.setState({
      carts: newCarts,
    });
  };

  render() {
    return (
      <Layout>
        <div className="cart-page">
          <span className="cart-title">Shopping Cart</span>
          <div className="cart-list">
            {this.state.carts.map((cart) => (
              <CartItem
                key={cart.id}
                cart={cart}
                updateCart={this.updateCart}
              />
            ))}
          </div>
          <div className="cart-total">
            total:
            <span className="total-price">{this.totalPrice()}</span>
          </div>
        </div>
      </Layout>
    );
  }
}
