import React from "react";
import ToolBox from "./ToolBox";
import Product from "./Product";
import { Component } from "react";

class Products extends React.Component {
  products = [
    {
      name: "二叉鹿角蕨",
      id: "0001",
      tags: "含版材",
      image: "images/1.jpeg",
      price: 600,
      status: "available",
    },
    {
      name: "銀鹿鹿角蕨",
      id: "0002",
      tags: "含版材",
      image: "images/2.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "象耳鹿角蕨",
      id: "0003",
      tags: "含版材",
      image: "images/3.jpeg",
      price: 700,
      status: "available",
    },
    {
      name: "普通鹿角蕨",
      id: "0004",
      tags: "4寸盆、不含版材",
      image: "images/4.jpeg",
      price: 600,
      status: "available",
    },
    {
      name: "山蘇",
      id: "0005",
      tags: "含盆",
      image: "images/5.jpeg",
      price: 400,
      status: "available",
    },
    {
      name: "愛麗絲鹿角蕨",
      id: "0006",
      tags: "含版材",
      image: "images/6.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "賽爾索鹿角蕨",
      id: "0007",
      tags: "含版材",
      image: "images/7.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "侏儒深綠鹿角蕨",
      id: "0008",
      tags: "含版材",
      image: "images/8.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "馬達加斯加園盾鹿角蕨",
      id: "0009",
      tags: "含版材",
      image: "images/9.png",
      price: 1200,
      status: "available",
    },
    {
      name: "窗簾蕨",
      id: "0010",
      tags: "含版材",
      image: "images/10.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "細葉亞洲猴腦鹿角蕨",
      id: "0011",
      tags: "含版材",
      image: "images/11.jpeg",
      price: 1200,
      status: "available",
    },
    {
      name: "蕾絲蕨",
      id: "0012",
      tags: "含版材",
      image: "images/12.jpeg",
      price: 1200,
      status: "available",
    },
  ];

  componentDidMount() {
    fetch("http://localhost:3004/")
      .then((response) => response.json())
      .then((data) => console.log(data));
  }

  render() {
    return (
      <div>
        <ToolBox />
        <div className="products">
          <div className="columns is-multiline is-desktop">
            {this.products.map((product) => (
              <div className="column is-3" key={product.id}>
                <Product product={product} />
              </div>
            ))}
          </div>
        </div>
      </div>
    );
  }
}

export default Products;
